package CH02;

public class C01진수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//문제  진수법
		
		//선생님 설명
		
//		7	5
//		000	000
//		111	101
//		111101 = 32 + 16 + 18 + 4 +1	= 61
//
//		7    5
//		56 + 5 = 61
//
//		4	5
//		000	101
//		100	101
//		00100101	=	32+4+1 =37
//
//
//
//		16진수(푸는법)
//		0
//		~
//		15	=	1111
//
//
//		9	A
//		0000	0000
//		1001	1010
//		10011010	= 128 + 16 +8 +2 =
//
//
//
//
//
//		8진수
//		0-7
//		0	0
//		1	1
//		2	10
//		3	11
//		4	110
//		5	101
//		6	110
//		7	111
		
		
		// 01 진수 개념
		// 02 10->2진수 변환
		// 03 2->10진수 변환
		// 04 문제
		//1		1	1	1	1	1	1	1
		//128	64	32	16	8	4	2	1

		//----------------------------------------------------------
		//문제  2진수 -> 10진수
		//----------------------------------------------------------
		//1000010(2) 	-> 	128+2		=	130(10)
		//1101010(2) 	-> 	128+64+8+2	=	202(10)
		//1001110(2) 	-> 	128+8+4+2	=	142(10)
		//0101100(2) 	-> 	64+8+4		= 	76 (10)
		//1001110(2) 	-> 	128 + 8+4+2 =	142(10)
		//
		//
		//----------------------------------------------------------
		//문제  8진수 -> 10진수
		//----------------------------------------------------------
		//75(8)		->	56 + 1	=	(10) 00 111 101 , 32+16+8+4+1
		//45(8)		->	32 + 1	=	(10) 00 100 101 , 32+4+1
		//112(8)	->	8+8+1	=	(10) 001 001 010, 64+8+2
		//19(8)		->	8+1		=	(10) 00 001 101 ,8+4+1
		//68(8)		->	48+1	=	(10)
		//55(8)		->	40+1	=	(10)
		//
		//----------------------------------------------------------
		//문제  16진수 -> 10진수
		//----------------------------------------------------------
		//9A(16)		->0000 1010,  1001 1010		(10)
		//16(16)		->00000000 ,  0001 0110		(10)
		//55(16)		->		(10)
		//FF(16)		->		(10)
		//D9(16)		->00000000 ,  1101 1001		(10)
		//3B(16)		->		(10)
		
//		7    5
//		111  101
//		00111101 = 32 + 16 + 8 + 4 + 1
//				=61
//
//
//		4     5
//		100   101
//		00100101 = 32 + 4 + 1 = 37
//
//
//		9A
//		10011010 = 128 + 16 + 8 + 2

		
		//10진수로 변환
		System.out.printf("10진수 : %d\n", 0b1111);		// 0b	: 2진수
		System.out.printf("10진수 : %d\n", 0b10101101);	// 0b	: 2진수
		System.out.printf("10진수 : %d\n", 173);  		//		: 10진수
		System.out.printf("10진수 : %d\n", 0255); 		// 0	: 8진수
		System.out.printf("10진수 : %d\n", 0xAD); 		// 0x 	: 16진수
		System.out.println();
		// 8진수로 변환
		System.out.printf("08진수 : %o\n", 0b10101101);	// 0b	: 2진수
		System.out.printf("08진수 : %o\n", 173);  		//		: 10진수
		System.out.printf("08진수 : %o\n", 0255); 		// 0	: 8진수
		System.out.printf("08진수 : %o\n", 0xAD); 		// 0x 	: 16진수
		System.out.println();
		// 16진수로 변환
		System.out.printf("16진수 : %X\n", 0b10101101);	// 0b	: 2진수
		System.out.printf("16진수 : %X\n", 173);  		//		: 10진수
		System.out.printf("16진수 : %X\n", 0255); 		// 0	: 8진수
		System.out.printf("16진수 : %X\n", 0xAD); 		// 0x 	: 16진수
		System.out.println();
		
	}

}
